package soma

import utest._
import scoin._
import scoin.Protocol._

object BMMTest extends TestSuite {
  val tests = Tests {
    test("build spacechain bmm of length 10") {
      val bmm = new BMM(totalSize = 100, amount = 1234.sat, sequence = 3)
      bmm.precompute(1, 7)

      bmm.txs.size ==> 7
    }

    test("read two transactions in sequence") {
      val tx1 =
        Transaction.read(
          ""
        )
      val tx2 =
        Transaction.read(
          ""
        )
      val tx3 =
        Transaction.read(
          ""
        )

      val txs = Seq(tx1, tx2, tx3)

      val ser = java.io.ByteArrayOutputStream()
      writeCollection(txs, ser, PROTOCOL_VERSION)

      val de = readCollection[Transaction](
        java.io.ByteArrayInputStream(ser.toByteArray()),
        PROTOCOL_VERSION
      )

      assert(txs == de)
      assert(de.size == 3)
    }
  }
}
